pragma solidity ^0.8.9; //Kompilierer deklarierung

contract SmartContract { //ähnlich wie bei OOP Klassen

  address public sender;        //Variable deklarierung für ETH adresen
  address public empfaenger;

   //Funktion für Zahlung
  function deposit(address external_empfaenger) external payable { //external erluabt es diesen ausserhalb des contracts abzurufen // payable ist ein modifikator für Ether
      require(msg.value > 0); //Bedingung das ether größer sien muss als 0 sonst wir diese snicht ausgeführt
      sender = msg.sender; //wer sendet das Geld wird so gespeichert
      empfaenger = external_empfaenger;
    }
                                // calling the account where the money beeing send to tahta account// getting a address with "this" is für this address contracts
}
